import React, { useState, useEffect } from 'react';
import { fetchPlanos } from '../api/veiculos';

const ParcelasModal = ({ veiculo, onClose }) => {
  const [planos, setPlanos] = useState([]);
  const [selectedPlano, setSelectedPlano] = useState(null);
  const [nome, setNome] = useState('');
  const [telefone, setTelefone] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [estado, setEstado] = useState(null);

  // Função para buscar o estado automaticamente
  useEffect(() => {
    const fetchEstado = async () => {
      try {
        const response = await fetch('https://ipapi.co/json/');
        const data = await response.json();
        if (data.region_code) {
          setEstado(data.region_code);
        } else {
          console.error('Erro ao obter o estado, usando valor padrão.');
          setEstado('ES'); // Fallback padrão
        }
      } catch (error) {
        console.error('Erro ao buscar o estado:', error);
        setEstado('ES'); // Fallback caso a API falhe
      }
    };

    fetchEstado();
  }, []);

  // Busca os planos após obter o estado e o veículo
  useEffect(() => {
    if (!estado || !veiculo.slug) return;

    const getPlanos = async () => {
      try {
        setLoading(true);
        const data = await fetchPlanos(estado, veiculo.slug);
        console.log("Dados retornados pela API:", data);

        if (data.plans && data.plans.length > 0) {
          const planosComSeguro = data.plans.map((plano) => ({
            nome: plano.name,
            parcelas: plano.options.withInsurance || [],
          }));
          setPlanos(planosComSeguro);
        } else {
          console.error("Estrutura da API inesperada ou sem dados:", data);
          setPlanos([]);
        }
      } catch (error) {
        console.error("Erro ao buscar planos:", error);
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    getPlanos();
  }, [estado, veiculo.slug]);

  // Função para filtrar as imagens laterais
  const getLateralImages = () => {
    if (!veiculo.carrossel) return [];
    return veiculo.carrossel.flatMap(item =>
      item.images.filter(image => image.endsWith("lateral.png"))
    );
  };

  const lateralImages = getLateralImages();

  const handleWhatsAppClick = () => {
    if (!selectedPlano) {
      alert("Selecione um plano antes de continuar.");
      return;
    }

    if (!nome || !telefone) {
      alert("Por favor, preencha seu nome e telefone.");
      return;
    }

    const phoneNumber = "5511999999999"; // Substitua pelo número desejado
    const message = `Olá, meu nome é ${nome} (${telefone}). Tenho interesse no modelo de moto ${veiculo.name} com o valor de parcela R$ ${selectedPlano.value}.`;
    const url = `https://wa.me/${phoneNumber}?text=${encodeURIComponent(message)}`;
    window.open(url, "_blank");
  };

  // Funções para o carrossel
  const nextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % lateralImages.length);
  };

  const prevImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? lateralImages.length - 1 : prevIndex - 1
    );
  };

  if (loading) {
    return <p>Carregando...</p>;
  }

  if (error) {
    return <p>Erro: {error}</p>;
  }

  return (
    <div style={styles.modalOverlay}>
      <div style={styles.modalContent}>
        <h2 style={styles.modalTitle}>Planos de Parcelas para {veiculo.name}</h2>
        <hr style={styles.hr} />

        {/* Carrossel de fotos da moto */}
        {lateralImages.length > 0 ? (
          <div style={styles.carrosselContainer}>
            <button onClick={prevImage} style={styles.carrosselButton}>
              &lt;
            </button>
            <img
              src={`https:${lateralImages[currentImageIndex]}`}
              alt={veiculo.name}
              style={styles.carrosselImage}
            />
            <button onClick={nextImage} style={styles.carrosselButton}>
              &gt;
            </button>
          </div>
        ) : (
          <p>Nenhuma imagem disponível.</p>
        )}

        {/* Planos e parcelas */}
        <div style={styles.planosContainer}>
          {planos.length > 0 ? (
            planos.map((plano) => (
              <div key={plano.nome} style={styles.planoItem}>
                <h3>{plano.nome}</h3>
                <div style={styles.parcelasContainer}>
                  {plano.parcelas.map((parcela) => (
                    <div
                      key={parcela.uniqueId}
                      style={{
                        ...styles.parcelaCard,
                        border:
                          selectedPlano?.uniqueId === parcela.uniqueId
                            ? '2px solid #007bff'
                            : '1px solid #ccc',
                      }}
                      onClick={() => setSelectedPlano(parcela)}
                    >
                      <p>
                        <strong>{parcela.numberInstallments}x</strong> de R$ {parcela.value}
                      </p>
                    </div>
                  ))}
                </div>
              </div>
            ))
          ) : (
            <p>Nenhum plano disponível.</p>
          )}
        </div>

        {/* Campos para nome e telefone */}
        <div style={styles.formContainer}>
          <input
            type="text"
            placeholder="Seu nome"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
            style={styles.input}
          />
          <input
            type="text"
            placeholder="Seu telefone"
            value={telefone}
            onChange={(e) => setTelefone(e.target.value)}
            style={styles.input}
          />
        </div>

        {/* Botões */}
        <button style={styles.whatsappButton} onClick={handleWhatsAppClick}>
          WhatsApp
        </button>
        <button style={styles.closeButton} onClick={onClose}>
          Fechar
        </button>
      </div>
    </div>
  );
};

export default ParcelasModal;
