import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchVeiculos } from '../api/veiculos';

const DetalhesVeiculo = () => {
  const [veiculo, setVeiculo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const { id } = useParams();

  useEffect(() => {
    const getVeiculo = async () => {
      try {
        // Simulação de busca de um veículo específico (substitua pela API real)
        const data = await fetchVeiculos('ES', 'motos'); // Exemplo
        const veiculoSelecionado = data.find((v) => v.id === id);
        if (veiculoSelecionado) {
          setVeiculo(veiculoSelecionado);
        } else {
          throw new Error('Veículo não encontrado');
        }
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    getVeiculo();
  }, [id]);

  if (loading) return <div>Carregando detalhes do veículo...</div>;
  if (error) return <div>Erro: {error}</div>;

  return (
    <div>
      <h1>{veiculo.lesma}</h1>
      <p>{veiculo.descricao}</p>
      <p><strong>Modelo:</strong> {veiculo.modelo}</p>
      <p><strong>Ano:</strong> {veiculo.ano}</p>
      <p><strong>Preço:</strong> R$ {veiculo.preçoParcelamento}</p>
    </div>
  );
};

export default DetalhesVeiculo;
