// src/api/veiculos.js
const API_BASE_URL = "https://www.consorcionacionalhonda.com.br/api";

export const fetchVeiculos = async (estado, categoria) => {
  try {
    const response = await fetch(`${API_BASE_URL}/veiculos/categorias/list/${estado}/${categoria}`);
    if (!response.ok) {
      throw new Error(`Erro na requisição: ${response.status}`);
    }
    const data = await response.json();
    return data.vehicles[0].vehicles || [];
  } catch (error) {
    console.error("Erro ao buscar veículos:", error);
    throw error;
  }
};

// Função para buscar as parcelas de um veículo específico
export const fetchParcelas = async (id) => {
  try {
    const response = await fetch(`${API_BASE_URL}/veiculo/plans/${id}`);
    if (!response.ok) {
      throw new Error(`Erro na requisição: ${response.status}`);
    }
    const data = await response.json();
    return data.parcelas || []; // Retorna a lista de parcelas
  } catch (error) {
    console.error("Erro ao buscar parcelas:", error);
    throw error;
  }
};
